function checkRegex(type,value) {
  switch (type) {
    case 'email':
      if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(value))
        return true;
      break;
    case 'tel':
      if (value.match(/^([0-9\-+])+$/))
        return true;
      break;
    default:
  }
  return false;
}
